function stepfinal() {
    jQuery("#p_body_content").fadeOut("slow");
    jQuery("#p_loading").fadeIn("slow");
}

function goToUrlFinish() {
    stepfinal();
    PreventExitPop = false;
    document.getElementById("p_form_post").submit();
}

function scrollTo(a) {
    if ($("#" + a).length) {
        var c = $("#" + a).offset();
        var b = c.top;
        $("html,body").animate({ scrollTop: b }, { duration: "slow" });
    }
}