$(document).ready(function () {
    $(".startform1").click(function () {
        $(".startform1").fadeOut("slow", function () {
            $("#q1").fadeIn("slow");
            $("#timerwrap").fadeIn("slow");
        });
    }),
    $(".bq1").click(function () {
        $("#q1").fadeOut("slow", function () {
            $("#q2").fadeIn("slow");
        });
    }),
        $(".bq2").click(function () {
            $("#q2").fadeOut("slow", function () {
                $("#q3").fadeIn("slow");
            });
        }),
        $(".bq3").click(function () {
            $("#q3").fadeOut("slow", function () {
                $("#q4").fadeIn("slow");
            });
        }),
        $(".bq4").click(function () {
            $("#q4").fadeOut("slow", function () {
                $("#q5").fadeIn("slow");
            });
        }),
        $(".bq5").click(function () {
            scrollTo("id1"),
                $("#content1").fadeOut("slow", function () {
                    $("#content2").fadeIn(),
                        setTimeout(function () {
                            $("#result1").fadeIn(1e3);
                        }, 3e3),
                        setTimeout(function () {
                            $("#result2").fadeIn(1e3);
                        }, 4100),
                        setTimeout(function () {
                            $("#result3").fadeIn(1e3);
                        }, 6e3),
                        setTimeout(function () {
                            $("#content2").fadeOut("slow", function () {
                                "undefined" != typeof roulette_ini ? rouletteRoot._init() : "undefined" != typeof box_ini && boxRoot._init(), $("#content3").fadeIn();
                            });
                        }, 7100);
                });
        });
});

